      subroutine SD_READ_SMINPUTS(ninlha,smval,done)

      implicit double precision (a-h,m,o-z)
      double precision smval(20)
      character line1*1
      logical done

      done=.false.

      do i=1,20,1
         smval(i) = 0.D0
