     .      -g2**2/dsbob(i)/dz*oppl(ni,nj)*(
     .      ((-astr(i,ni)*astr(i,nj)+bstr(i,ni)*bstr(i,nj))*vzz+
     .       (astr(i,ni)*astr(i,nj)+bstr(i,ni)*bstr(i,nj))*azz)*
     .      xmneut(nj)/xmneut(ni)*xmus*(
     .      1/xmuz*(-4.D0)*(1.D0+xmuneut1+2.D0*xmus-uh-th)+16.D0) +
     .      ((-astr(i,ni)*astr(i,nj)+bstr(i,ni)*bstr(i,nj))*vzz-
