     .          (achar(i,ni)*achar(k,ni)+bchar(i,ni)*bchar(k,ni))*
     .          (alschar(i,nj)*alschar(k,nj)+akschar(i,nj)*
     .              akschar(k,nj))*
     .          (xmuneut1*(-xmuc+uh-1.D0)+xmuc*(uh-xmus)+xmus*
     .          (uh-1.D0)+uh*(-uh+1.D0))+
     .          (achar(i,ni)*achar(k,ni)+bchar(i,ni)*bchar(k,ni))*
