      dz  = 1.D0-x3+xmust1-xmuz

      vzz = vzzbotbot
      azz = azzbotbot

c -------------------------------------------------------------------- c
c                              Z exchange
c -------------------------------------------------------------------- c

      st2st1zz = 1.D0/4.D0/cw**2/dz**2*aztt12**2*
     .     2.D0*(vzz**2+azz**2)*4.D0*(1.D0+x1*x2-x1-x2-xmust1)

c -------------------------------------------------------------------- c

      SD_st2st1dd=st2st1zz

      end

