      double precision function SD_sb2sb1uu(x1,x2)

      implicit double precision (a-h,m,o-z)
      dimension gztt(2,2),gzbb(2,2),gztautau(2,2)

      COMMON/SD_param/gf,amz,amw,pi,g2
