      dimension alto(2,2)
      dimension alsneu(2),aksne(2)
      dimension aw(2,2)
      dimension p0(4),pb(4),pf(4),pfb(4),pw(4),psb(4),pt(4),pchi(4)
      dimension psto(4),psn(4),dsto(2),dsb(2),dchi(2)
      dimension p(4)

      COMMON/SD_IRUN4BOD/IRUNZ
